const flash=(message,type="success",close=!0)=>{let alert=`<div class="alert alert-${type} alert-dismissible fade show" role="alert">\n                        <strong>${message}</strong>\n                        ${close=!0===close?'<button type="button" class="btn-close" data-dismiss="alert" aria-label="Close"></button>':""}\n                   </div>`;document.querySelector(".flash").innerHTML=alert,setTimeout(()=>{document.querySelector('[role="alert"]').classList.replace("show","hide")},5e3)};